\(\begin{flalign*}
\text{Contoh 1. }
&\text{Jika }P(x) = 2x^3 - 5x^2 - 4x + 1\text{ dan }Q(x) = 3x^2 - x - 2, \\
&\text{tentukan }P(x)+Q(x)\text{ dan }P(x)-Q(x)
\end{flalign*}\)



\(\begin{flalign*}
P(x) + Q(x) &= (2x^3 - 5x^2 - 4x + 1) + (3x^2 - x - 2) \\
&= 2x^3 + (-5+3)\,x^2 + (-4+(-1))\,x + (1+(-2))\\
&= 2x^3 - 2x^2 - 5x - 1
\end{flalign*}\)
dan
\(\begin{flalign*}
P(x) - Q(x) &= (2x^3 - 5x^2 - 4x + 1) - (3x^2 - x - 2) \\
&= 2x^3 + (-5-3)\,x^2 + (-4-(-1))\,x + (1-(-2))\\
&= 2x^3 + (-8)\,x^2 + (-4+1)\,x + (1+2)\\
&= 2x^3 - 8x^2 - 3x + 3
\end{flalign*}\)











\(\begin{flalign*}
\text{Contoh 2. }
&\text{Jika }P(x) = 2x^2 + x - 6 \text{ dan }Q(x) = 2x - 1, \\
&\text{tentukan }P(x)\times{Q(x)}
\end{flalign*}\)



\(\begin{flalign*}
P(x) \times Q(x) &= (2x^2 + x - 6) \times (2x - 1) \\
&= 2x^2\cdot(2x - 1) + x\cdot(2x - 1) - 6\cdot(2x - 1)\\
&= 4x^3 - 2x^2 + 2x^2 - x - 12x + 6 \\
&= 4x^3 - 13x + 6
\end{flalign*}\)











